mapper.select:
SELECT BRTC_CODE 
FROM V_CITY
WHERE BCITY_NM = #{bcityNm} AND BRTC_NM = #{brtcNm}

SELECT BCITY_NM 
FROM BCITY
WHERE BCITY_CODE = #{bcityCode}

SELECT BRTC_NM
FROM BRTC
WHERE BRTC_CODE = #{brtcCode}

SELECT SPCLTY_REALM_NM
FROM SPCLTY_REALM
WHERE SPCLTY_REALM_CODE=#{spcltyRealmCode}

		SELECT COUNT(*) FROM SRVC_REQUST
