mapper.select:
SELECT LBRTY_BBSCTT_NO, LBRTY_BBSCTT_SJ, USER_ID, USER_NCNM, DEL_TYPE FROM V_LBRTY_BBSCTT WHERE DEL_TYPE = '0'
AND USER_ID = #{userId}
order by lbrty_bbsctt_wr_dt DESC

mapper.delete:
DELETE FROM LBRTY_BBSCTT
WHERE 1=1 AND
	LBRTY_BBSCTT_NO = ${lbrtyBbscttNo}

mapper.update:
UPDATE LBRTY_BBSCTT SET LBRTY_BBSCTT_SJ = #{lbrtyBbscttSj},
LBRTY_BBSCTT_CN = #{lbrtyBbscttCn},
LBRTY_BBSCTT_WR_DT = sysdate WHERE lbrty_bbsctt_no = ${lbrtyBbscttNo}

mapper.delete:
DELETE FROM SPRVISE_ATCHMNFL
	WHERE 1=1 AND
		SPRVISE_ATCHMNFL_NO = ${sprviseAtchmnflNo}

mapper.insert:
    	insert into LBRTY_BBSCTT_ANSWER(
    		LBRTY_BBSCTT_ANSWER_NO, LBRTY_BBSCTT_NO, LBRTY_BBSCTT_ANSWER_CN, 
    		LBRTY_BBSCTT_ANSWER_WR_DT, USER_ID
		)values(
    		LBRTY_BBSCTT_ANSWER_NO_SEQ.nextval,${lbrtyBbscttNo},#{lbrtyBbscttAnswerCn},
    		sysdate,#{userId}
		)

mapper.delete:
delete from LBRTY_BBSCTT_ANSWER
where 1 = 1 and
	LBRTY_BBSCTT_ANSWER_NO = ${lbrtyBbscttAnswerNo}

mapper.select:
    	select count(*)
		from V_LBRTY_BBSCTT_ANSWER
			where PT_LBRTY_BBSCTT_ANSWER_NO = ${ptLbrtyBbscttAnswerNo}

mapper.delete:
    	delete from SPRVISE_ATCHMNFL
		where SPRVISE_ATCHMNFL_NO = ${sprviseAtchmnflNo} and
		ATCHMNFL_NO = ${atchmnflNo}

mapper.update:
    	update LBRTY_BBSCTT 
		set SPRVISE_ATCHMNFL_NO = ${sprviseAtchmnflNo}
		where LBRTY_BBSCTT_NO = ${lbrtyBbscttNo}

mapper.select:
WITH T AS( SELECT ROW_NUMBER() OVER(ORDER BY LBRTY_BBSCTT_WR_DT) RNUM, LBRTY_BBSCTT_NO, LBRTY_BBSCTT_SJ, LBRTY_BBSCTT_CN, LBRTY_BBSCTT_WR_DT, LBRTY_BBSCTT_RDCNT, SPRVISE_ATCHMNFL_NO, USER_ID, USER_NCNM, DEL_TYPE FROM V_LBRTY_BBSCTT WHERE 1 = 1 and DEL_TYPE=0
AND USER_ID = #{userId}
) SELECT T.* FROM T WHERE T.RNUM BETWEEN (#{total} - (10 * (#{currentPage} - 1)) - 9) AND (#{total} - (10 * (#{currentPage} - 1))) ORDER BY RNUM DESC

