SELECT COUNT(*) FROM (SELECT * FROM ONE_INQRY A LEFT JOIN ONE_INQRY_ANSWER B ON A.ONE_INQRY_NO = B.ONE_INQRY_NO WHERE A.ONE_INQRY_SJ NOT LIKE '%탈퇴%'
AND A.USER_ID = #{userId}
AND ${selectColumn} LIKE '%' || #{keyword} || '%'
AND (A.ONE_INQRY_SJ LIKE '%' || #{keyword} || '%' OR A.USER_ID LIKE '%' || #{keyword} || '%')
)

SELECT COUNT(*) FROM (SELECT * FROM ONE_INQRY A LEFT JOIN ONE_INQRY_ANSWER B ON A.ONE_INQRY_NO = B.ONE_INQRY_NO WHERE B.ONE_INQRY_ANSWER_CN IS NULL AND A.ONE_INQRY_SJ NOT LIKE '%탈퇴%'
AND A.USER_ID = #{userId}
AND ${selectColumn} LIKE '%' || #{keyword} || '%'
AND (A.ONE_INQRY_SJ LIKE '%' || #{keyword} || '%' OR A.USER_ID LIKE '%' || #{keyword} || '%')
)

