WITH T AS ( SELECT ROW_NUMBER() OVER (ORDER BY ONE_INQRY_WRITNG_DT DESC) NUM, B.* FROM( (SELECT A.* FROM (SELECT * FROM ONE_INQRY A LEFT JOIN ONE_INQRY_ANSWER B ON A.ONE_INQRY_NO = B.ONE_INQRY_NO WHERE A.ONE_INQRY_SJ NOT LIKE '%탈퇴%'
AND A.USER_ID = #{userId}
AND ${selectColumn} LIKE '%' || #{keyword} || '%'
AND (A.ONE_INQRY_SJ LIKE '%' || #{keyword} || '%' OR A.USER_ID LIKE '%' || #{keyword} || '%')
) A ) )B ) SELECT T.* FROM T WHERE T.NUM BETWEEN (#{currentPage}*10) - (10 - 1) AND (#{currentPage}*10)

